import React from 'react';
import { Table, TableBody, TableCell, TableContainer, TableHead, TableRow, Paper, IconButton } from '@mui/material';
import DeleteIcon from '@mui/icons-material/Delete';
import api from '../services/api';

const TransactionList = ({ transactions, refreshTransactions }) => {

  const deleteTransaction = async (id) => {
    try {
      await api.delete(`/transactions/${id}`);
      refreshTransactions();
    } catch (err) {
      console.error('Erreur lors de la suppression de la transaction');
    }
  };

  return (
    <TableContainer component={Paper}>
      <Table>
        <TableHead>
          <TableRow>
            <TableCell>Date</TableCell>
            <TableCell>Description</TableCell>
            <TableCell>Type</TableCell>
            <TableCell align="right">Montant</TableCell>
            <TableCell align="right">Actions</TableCell>
          </TableRow>
        </TableHead>
        <TableBody>
          {transactions.map((tx) => (
            <TableRow key={tx._id}>
              <TableCell>{new Date(tx.date).toLocaleDateString()}</TableCell>
              <TableCell>{tx.description}</TableCell>
              <TableCell>{tx.type === 'income' ? 'Revenu' : 'Dépense'}</TableCell>
              <TableCell align="right">{tx.amount}</TableCell>
              <TableCell align="right">
                <IconButton onClick={() => deleteTransaction(tx._id)}>
                  <DeleteIcon />
                </IconButton>
              </TableCell>
            </TableRow>
          ))}
        </TableBody>
      </Table>
    </TableContainer>
  );
};

export default TransactionList;
