// src/pages/Dashboard.js
import React, { useEffect, useState, useContext } from 'react';
import { AppBar, Toolbar, Typography, Button, Container, Box } from '@mui/material';
import { useNavigate } from 'react-router-dom';
import api from '../services/api';
import { AuthContext } from '../context/AuthContext';
import TransactionForm from '../components/TransactionForm';
import TransactionList from '../components/TransactionList';
import ChartDashboard from '../components/ChartDashboard';
import PeriodFilter from '../components/PeriodFilter';
import BalanceCard from '../components/BalanceCard';

const Dashboard = () => {
  const { logout, token } = useContext(AuthContext);
  const navigate = useNavigate();
  const [transactions, setTransactions] = useState([]);
  const [filter, setFilter] = useState({ period: 'Tous', startDate: '', endDate: '' });
  const [balanceRefreshTrigger, setBalanceRefreshTrigger] = useState(0);

  const fetchTransactions = async () => {
    try {
      const params = {};
      if (filter.period !== 'Tous') {
        params.startDate = filter.startDate;
        params.endDate = filter.endDate;
      }
      const res = await api.get('/transactions', { params });
      setTransactions(res.data);
    } catch (err) {
      console.error(
        'Erreur lors du chargement des transactions',
        err.response ? err.response.data : err.message
      );
    }
  };

  useEffect(() => {
    if (token) fetchTransactions();
  }, [token, filter]);

  const addTransaction = async (transactionData) => {
    try {
      const res = await api.post('/transactions', transactionData);
      // Mise à jour immédiate de la liste
      setTransactions([res.data, ...transactions]);
      // Incrémentation du trigger pour rafraîchir le solde global
      setBalanceRefreshTrigger((prev) => prev + 1);
    } catch (err) {
      console.error(
        'Erreur lors de l’ajout de la transaction',
        err.response ? err.response.data : err.message
      );
    }
  };

  const handleLogout = () => {
    logout();
    navigate('/login');
  };

  const handleFilterChange = (newFilter) => {
    setFilter(newFilter);
  };

  return (
    <>
      <AppBar position="static">
        <Toolbar>
          <Typography variant="h6" sx={{ flexGrow: 1 }}>
            Gestion d'argent
          </Typography>
          <Button color="inherit" onClick={handleLogout}>
            Déconnexion
          </Button>
        </Toolbar>
      </AppBar>
      <Container sx={{ mt: 4 }}>
        {/* Le solde global, qui ne dépend pas du filtre */}
        <BalanceCard refreshTrigger={balanceRefreshTrigger} />
        <PeriodFilter onFilterChange={handleFilterChange} />
        <Box sx={{ mb: 4 }}>
          <Typography variant="h5" gutterBottom>
            Ajouter une transaction
          </Typography>
          <TransactionForm addTransaction={addTransaction} />
        </Box>
        <Box sx={{ mb: 4 }}>
          <Typography variant="h5" gutterBottom>
            Statistiques
          </Typography>
          <ChartDashboard transactions={transactions} />
        </Box>
        <Box>
          <Typography variant="h5" gutterBottom>
            Transactions récentes
          </Typography>
          <TransactionList transactions={transactions} refreshTransactions={fetchTransactions} />
        </Box>
      </Container>
    </>
  );
};

export default Dashboard;
