// src/components/BalanceCard.js
import React, { useEffect, useState } from 'react';
import { Card, CardContent, Typography } from '@mui/material';
import api from '../services/api';

const BalanceCard = ({ startDate, endDate }) => {
  const [balance, setBalance] = useState(0);

  const fetchBalance = async () => {
    try {
      const params = {};
      if (startDate) params.startDate = startDate;
      if (endDate) params.endDate = endDate;
      const res = await api.get('/transactions/balance', { params });
      setBalance(res.data.balance);
    } catch (err) {
      console.error('Erreur lors du chargement du solde', err.response ? err.response.data : err.message);
    }
  };

  useEffect(() => {
    fetchBalance();
  }, [startDate, endDate]);

  return (
    <Card sx={{ mb: 2 }}>
      <CardContent>
        <Typography variant="h6">Solde actuel</Typography>
        <Typography variant="h4">{balance.toFixed(2)} EUR</Typography>
      </CardContent>
    </Card>
  );
};

export default BalanceCard;
