import React, { useState } from 'react';
import { TextField, Button, MenuItem, Box } from '@mui/material';

const TransactionForm = ({ addTransaction }) => {
  const [formData, setFormData] = useState({
    amount: '',
    date: '',
    type: 'expense',
    description: ''
  });

  const { amount, date, type, description } = formData;

  const onChange = (e) => {
    setFormData({ ...formData, [e.target.name]: e.target.value });
  };

  const onSubmit = (e) => {
    e.preventDefault();
    if (!amount || !date) return;
    const transactionData = {
      amount: parseFloat(amount), // conversion explicite en nombre
      date,
      type,
      description
    };
    addTransaction(transactionData);
    setFormData({
      amount: '',
      date: '',
      type: 'expense',
      description: ''
    });
  };


  return (
    <Box component="form" onSubmit={onSubmit} sx={{ display: 'flex', gap: 2, flexWrap: 'wrap' }}>
      <TextField 
        label="Montant" 
        name="amount" 
        type="number" 
        value={amount} 
        onChange={onChange} 
        required 
      />
      <TextField 
        label="Date" 
        name="date" 
        type="date" 
        value={date} 
        onChange={onChange} 
        InputLabelProps={{ shrink: true }} 
        required 
      />
      <TextField 
        select 
        label="Type" 
        name="type" 
        value={type} 
        onChange={onChange}
      >
        <MenuItem value="expense">DÃ©pense</MenuItem>
        <MenuItem value="income">Revenu</MenuItem>
      </TextField>
      <TextField 
        label="Description" 
        name="description" 
        value={description} 
        onChange={onChange} 
      />
      <Button type="submit" variant="contained">Ajouter</Button>
    </Box>
  );
};

export default TransactionForm;
